	for INTERFACE_NAME in ${ALL_INTERFACE_NAMES};
	do
		INTERFACE_NAME_NO_UNDERSCORE=$(echo ${INTERFACE_NAME} | sed -e 's/_/ /g') # Replace _ with a space for readability
